$(document).ready(function () {
	var attack = $('.attacking');             //进攻
	var dattack = $('.dangerousattacking');   //危险进攻
	var layup  = $('.layup');                 //1分球 带球上篮
	var taiotou  = $('.taiotou');             //2分球 跳投
	var sanfen  = $('.sanfen');               //3分球 倒退跳投
	var guanlan  = $('.guanlan');             //灌篮
	var goalshot = $('.goalshot');            //进球
    var faqiu1 = $('.faqiu1');                //罚球1 球进shot 大西瓜投篮
    var faqiu2 = $('.faqiu2');                //罚球2 罚球未进miss 大西瓜投篮
    var fangui  = $('.fangui');               //犯规
    var zhanting = $('.zhanting');            //暂停
	var homesc = $('.home-score')
	var awaysc = $('.away-score')


	function stopmv(h){setTimeout(function () { h.addClass('hide').removeClass('home').removeClass('away');h.find('.team-name').text('');}, 5600);} //停止播放动画

	function home(e){e.addClass('home').removeClass('hide');var teamhome = $('.animate-team-bottom .left .name').text();$('.home .team-name').text(teamhome);stopmv(e);} //播放主队
	function away(e){e.addClass('away').removeClass('hide');var teamaway = $('.animate-team-bottom .right .name').text();$('.away .team-name').text(teamaway);stopmv(e);} //播放客队
	function scorechange(e){e.addClass('change');setTimeout(function(){e.removeClass('change');}, 5600);} //得分变色

	//动画加载中
	setTimeout(function(){$('#animation-loading').removeClass('hide');setTimeout(function () { $('#animation-loading').addClass('hide');}, 1500);}, 0); 

	//载入草皮
	setTimeout(function(){$('#grassLand').removeClass('hide');}, 2000);

	//载入篮框
	setTimeout(function(){$('.bkt-live-bgleft, bkt-live-bgright, .bkt-live-bgleft2, bkt-live-bgright2').removeClass('hide');}, 2300);

	//即将开始 20170817 #board .status
	setTimeout(function(){
		$('#board').removeClass('hide');
		$('#board .status, .match-tips').html('即将开始');
		setTimeout(function () {$('#board').addClass('hide');}, 1500);
	}, 2500);

	setTimeout(function(){home(attack);}, 5600);      //[主]进攻
	setTimeout(function(){home(dattack);}, 11200);    //[主]危险进攻
	setTimeout(function(){home(guanlan);}, 16800);    //[主]灌篮
	setTimeout(function(){home(layup);}, 22400);      //[主]1分球 带球上篮
	setTimeout(function(){home(taiotou);}, 28000);    //[主]2分球 跳投
	setTimeout(function(){home(sanfen);}, 33600);     //[主]3分球 倒退跳投
	setTimeout(function(){home(goalshot);scorechange(homesc);}, 39200);   //[主]进球 得分变色
	setTimeout(function(){home(faqiu1);}, 44800);    //[主]罚球1 球进shot 大西瓜投篮
	setTimeout(function(){home(faqiu2);}, 50400);    //[主]罚球2 罚球未进miss 大西瓜投篮
	setTimeout(function(){home(fangui);}, 56000);     //[主]犯规
	setTimeout(function(){home(zhanting);}, 61600);   //[主]暂停

	setTimeout(function(){away(attack);}, 67200);      //[客]进攻
	setTimeout(function(){away(dattack);}, 72800);    //[客]危险进攻
	setTimeout(function(){away(guanlan);}, 78400);    //[主]灌篮
	setTimeout(function(){away(layup);}, 84000);      //[客]1分球 带球上篮
	setTimeout(function(){away(taiotou);}, 89600);    //[客]2分球 跳投
	setTimeout(function(){away(sanfen);}, 95200);     //[客]3分球 倒退跳投
	setTimeout(function(){away(goalshot);scorechange(awaysc);}, 100800);   //[客]进球 得分变色
	setTimeout(function(){away(faqiu1);}, 106400);    //[客]罚球1 罚球球进shot 大西瓜投篮
	setTimeout(function(){away(faqiu2);}, 112000);    //[客]罚球2 罚球未进miss 大西瓜投篮
	setTimeout(function(){away(fangui);}, 117600);     //[客]犯规
	setTimeout(function(){away(zhanting);}, 123200);   //[客]暂停

	//比赛结束
	setTimeout(function(){
		$('.home-data .corner-kick').html('1'); //[主]角球 
		$('.home-data .yellow-card').html('2'); //[主]黄牌
		$('.home-data .red-card').html('0'); //[主]红牌
		$('.home-data .shoots').html('12'); //[主]射门
		$('.home-data .control').html('47%'); //[主]控球率
		$('.away-data .corner-kick').html('2'); //[客]角球 
		$('.away-data .yellow-card').html('3'); //[客]黄牌
		$('.away-data .red-card').html('0'); //[客]红牌
		$('.away-data .shoots').html('4'); //[客]射门
		$('.away-data .control').html('53%'); //[客]控球率
		$('#board').removeClass('hide');$('#board .status').html('已完赛');},128800);

}); //最後的括號勿刪除