$(document).ready(function () {

	var attack = $('.attacking');             //进攻
	var dattack = $('.dangerousattacking');   //危险进攻
	var goalshot = $('.goalshot');            //进球
	var shotoff = $('.shotoff');              //射偏
	var cornerkick = $('.cornerkick');        //角球
	var freekick = $('.freekick');            //任意球
	var outkick = $('.outkick');              //界外球
	var goalkick = $('.goalkick');            //球门球
	var teamtrap = $('.teamtrap');            //控球
	var huanren = $('.huanren');              //换人
	var rdcard = $('.card-tip.red');         //红牌
	var ycard = $('.card-tip.yellow');       //黄牌
	var yrcard = $('.card-tip.yellowtored'); //两黄变红
	var injury = $('.injury');                //受伤
	var homesc = $('.home-score')
	var awaysc = $('.away-score')

	function stopmv(h){setTimeout(function () { h.addClass('hide').removeClass('home').removeClass('away');h.find('.team-name').text('');}, 5600);} //停止播放动画

	function home(e){e.addClass('home').removeClass('hide');var teamhome = $('.panel .home .name').text();$('.home .team-name').text(teamhome);stopmv(e);} //播放主队
	function away(e){e.addClass('away').removeClass('hide');var teamaway = $('.panel .away .name').text();$('.away .team-name').text(teamaway);stopmv(e);} //播放客队

	function scorechange(e){e.addClass('change');setTimeout(function(){e.removeClass('change');}, 5600);} //得分变色

	//动画加载中
	setTimeout(function(){$('#animation-loading').removeClass('hide');setTimeout(function () { $('#animation-loading').addClass('hide');}, 1500);}, 0); 

	//载入草皮
	setTimeout(function(){$('#grassLand, #courtLayer').removeClass('hide');}, 2000); 

	//即将开始 20170817 #board .status
	setTimeout(function(){$('#board').removeClass('hide');$('#board .status, .match-tips').html('即将开始');setTimeout(function () {$('#board').addClass('hide');}, 1500);}, 2500); 

	setTimeout(function(){home(attack);}, 5600);      //[主]进攻
	setTimeout(function(){home(dattack);}, 11200);     //[主]危险进攻
	setTimeout(function(){home(goalshot);scorechange(homesc);}, 16800);   //[主]进球
	setTimeout(function(){home(shotoff);}, 22400);    //[主]射偏
	setTimeout(function(){home(cornerkick);}, 28000); //[主]角球
	setTimeout(function(){home(freekick);}, 33600);   //[主]任意球
	setTimeout(function(){home(outkick);}, 39200);    //[主]界外球
	setTimeout(function(){home(goalkick);}, 44800);   //[主]球门球
	setTimeout(function(){home(teamtrap);}, 50400);   //[主]控球
	setTimeout(function(){home(huanren);}, 56000);    //[主]换人
	setTimeout(function(){home(rdcard);}, 61600);     //[主]红牌
	setTimeout(function(){home(ycard);}, 67200);      //[主]黄牌
	setTimeout(function(){home(yrcard);}, 72800);     //[主]两黄变红
	setTimeout(function(){home(injury);}, 78400);     //[主]受伤

	setTimeout(function(){away(attack);}, 84000);     //[客]进攻
	setTimeout(function(){away(dattack);}, 89600);    //[客]危险进攻
	setTimeout(function(){away(goalshot);scorechange(awaysc);}, 95200);   //[客]进球
	setTimeout(function(){away(shotoff);}, 100800);    //[客]射偏
	setTimeout(function(){away(cornerkick);}, 106400);//[客]角球
	setTimeout(function(){away(freekick);}, 112000);  //[客]任意球
	setTimeout(function(){away(outkick);}, 117600);   //[客]界外球
	setTimeout(function(){away(goalkick);}, 123200);  //[客]球门球
	setTimeout(function(){away(teamtrap);}, 128800);  //[客]控球
	setTimeout(function(){away(huanren);}, 134400);   //[客]换人
	setTimeout(function(){away(rdcard);}, 140000);    //[客]红牌
	setTimeout(function(){away(ycard);}, 145600);     //[客]黄牌
	setTimeout(function(){away(yrcard);}, 151200);    //[客]两黄变红
	setTimeout(function(){away(injury);}, 156800);    //[客]受伤

	//伤停加时 20170817
	setTimeout(function(){

		$('#tips').removeClass('hide');
		$('#tips .status .text').html('伤停加时');
		$('#tips .status .minute-label').html('1分钟');
		$('.panel .score-area .upper .ot').html('（+1分钟）');

		//时间到移除
		setTimeout(function () { 
		$('#tips .status .minute-label').html('');
		$('#tips .status .text').html('');
		$('#tips').addClass('hide');
		}, 4500);

	}, 162400);    //不分主客

	//比赛结束
	setTimeout(function(){

		$('.home-data .corner-kick').html('1'); //[主]角球 
		$('.home-data .yellow-card').html('2'); //[主]黄牌
		$('.home-data .red-card').html('0'); //[主]红牌
		$('.home-data .shoots').html('12'); //[主]射门
		$('.home-data .control').html('47%'); //[主]控球率

		$('.away-data .corner-kick').html('2'); //[客]角球 
		$('.away-data .yellow-card').html('3'); //[客]黄牌
		$('.away-data .red-card').html('0'); //[客]红牌
		$('.away-data .shoots').html('4'); //[客]射门
		$('.away-data .control').html('53%'); //[客]控球率

		$('#board').removeClass('hide');$('#board .status, .match-tips').html('完赛');},168000); //20170817 #board .status

}); //最後的括號勿刪除